import re
import time
import datetime
import traceback

import requests


class XiaomiSport():

    def __init__(self):
        self.access_code = ''
        self.app_token = ''
        self.user_id = ''

        self.headers = {
            'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
            'User-Agent': 'Dalvik/2.1.0 (Linux; U; Android 5.1.1; SM-G9750 Build/LMY48Z)'
        }

    def login(self, phone, password):
        url = 'https://api-user.huami.com/registrations/' + phone + '/tokens'
        print(url)
        data = {
            'phone_number': phone,
            'password': password,
            'client_id': 'HuaMi',
            'token': 'access',
            'redirect_uri': 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html'
        }

        response = requests.post(url, data=data, headers=self.headers)
        print(response.status_code)
        if response.status_code != 200:
            return 'the request has failed'
        else:
            print('the request has succeeded')
            # Location
            redirect_location = response.history[0].headers['Location']
            print(redirect_location)
            # 获取通行码
            find_access = re.compile(r'access=(.*?)&')
            access_code = find_access.findall(redirect_location)
            print(access_code)
            if len(access_code) == 0:
                return '账号密码对不上，登录失败'
            else:
                self.access_code = access_code[0]
                return '登录成功'

    def get_token(self):
        '''
        获取令牌
        '''
        url = 'https://account.huami.com/v2/client/login'
        data = {
            'third_name': 'huami_phone',
            'grant_type': 'access_token',
            'device_model': 'android_phone',
            'device_id': '865166029072108',
            'country_code': 'CN',
            'code': self.access_code,
            'app_version': '4.6.1',
            'app_name': 'com.xiaomi.hm.health'
        }

        response = requests.post(url, headers=self.headers, data=data)
        if response.status_code != 200:
            return '获取令牌失败'
        else:
            if len(eval(response.text)) == 1:   # {'error_code': '0100'}
                return '获取令牌失败'
            else:
                token = eval(response.text)['token_info']
                self.app_token = token['app_token']
                self.user_id = token['user_id']
                return '获取令牌成功'

